/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
const minZeroArray = function (nums, queries) {
	const q = queries.length;
	const n = nums.length;
	const dp = Array.from({ length: n }, () => new Array(1001).fill(false));

	for (let i = 0; i < n; i++) {
		dp[i][0] = true;
	}

	if (nums.every((x) => x == 0)) return 0;

	for (let i = 0; i < q; i++) {
		const [l, r, val] = queries[i];

		for (let j = l; j <= r; j++) {
			const x = new Array(1001).fill(false);

			for (let k = 0; k < 1001 - val; k++) {
				if (dp[j][k]) {
					x[k + val] = true;
				}
			}

			for (let k = 0; k < 1001; k++) {
				dp[j][k] ||= x[k];
			}

			for (let k = nums[j] + 1; k < 1001; k++) {
				dp[j][k] = false;
			}
		}

		let ok = true;
		for (let i = 0; i < n; i++) {
			if (!dp[i][nums[i]]) {
				ok = false;
				break;
			}
		}

		if (ok) return i + 1;
	}

	return -1;
};

