class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int maxi = *max_element(nums.begin(), nums.end());
        vector<vector<int>> dp(n, vector<int>(maxi + 1, -1));
        for (int i = 0; i < n; i++) {
            dp[i][0] = 0;
            for (int t = 1; t <= maxi; t++) dp[i][t] = -1;
        }
        for (int i = 0; i < m; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int index = l; index <= r; index++) {
                for (int t = maxi; t >= val; t--) {
                    if (dp[index][t - val] != -1) {
                        if (dp[index][t] == -1)
                            dp[index][t] = i + 1;
                        else
                            dp[index][t] = min(dp[index][t], i + 1);
                    }
                }
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            int mini_k = dp[i][nums[i]];
            if (mini_k == -1) return -1;
            ans = max(ans, mini_k);
        }
        return ans;
    }
};

