class Solution {
public:
    int dp[1005][1005];

    int solve(vector<pair<int,int>> &sumArray,int index,int n,int num){

        if(num==0){
            return sumArray[index-1].second;
        }
        if(num<0){
            return 1e4;
        }
        if(index>=n){
            return 1e4;
        }

        if(dp[index][num]!=-1){
            return dp[index][num];
        }

        int ans=min(solve(sumArray,index+1, n,num-sumArray[index].first), solve(sumArray, index+1, n, num));

        return dp[index][num]= ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans=-1;
        int cnt=0;

        for(int i=0;i<nums.size();i++){
            int num=nums[i];

            if(num==0){
                cnt++;
                continue;
            }

            // this has to be made zero

            // check if there is a subsequence of query val that can get me the. particular sum
            vector<pair<int,int>> sumArray;

            int index=0;

            for(auto query:queries){
                int l=query[0];
                int r=query[1];

                if(l<=i && r>=i){
                    sumArray.push_back({query[2],index});
                }
                index++;
            }


            // find the possiblity of subsequence sum of num, with the least queryIndex possible

            memset(dp,-1,sizeof(dp));

            int indexx=solve(sumArray,0,sumArray.size(), nums[i]);


            if(indexx<1e4){
                ans=max(ans, indexx+1);
            }
            else{
                ans=-1;
                break;
            }

            // check which is the biggest array that encompasses thsi query
        }

        if(cnt==nums.size()){
            return 0;
        }

        return ans;
    }
};

