class Solution {
public:
    int rec(int sum, vector<pair<int, int>>& val, int ind, vector<unordered_map<int, int>>& dp) {
        if (sum == 0)
            return (ind > 0) ? val[ind - 1].second : 0;
        if (sum < 0 || ind >= val.size())
            return INT_MAX;
        if (dp[ind].find(sum) != dp[ind].end())
            return dp[ind][sum];
        // take or not take
        return dp[ind][sum] = min(rec(sum - val[ind].first, val, ind + 1, dp), rec(sum, val, ind + 1, dp));
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<pair<int, int>>> vals(n);
        int s = queries.size();
        for (int k = 0; k < s; k++) {
            auto q = queries[k];
            int l = q[0];
            int r = q[1];
            int val = q[2];
            for (int i = 0; i < n; i++) {
                if (i >= l && i <= r) {
                    vals[i].push_back({val, k + 1});
                }
            }
        }
        int res = 0;
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0)
                continue;
            vector<unordered_map<int, int>> dp(vals[i].size());
            int ans = rec(nums[i], vals[i], 0, dp);
            if (ans == INT_MAX)
                return -1;
            res = max(res, ans);
        }
        return res;
    }
};

