class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), k = 0, cnt = 0;

        unordered_set<int> s, t;

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                cnt++;
                continue;
            }

            s.clear();

            for (int j = 0; j < m; j++) {
                auto query = queries[j];
                if (query[0] <= i && i <= query[1]) {
                    t = s;
                    for (auto ele : s) {
                        // printf("%d, ", ele + query[2]);
                        t.insert(ele + query[2]);
                    }
                    t.insert(query[2]);
                    s = t;
                }

                // for (auto&& ele : s) {
                //     printf("%d, ", ele);
                // }
                // printf("n");

                if (s.find(nums[i]) != s.end()) {
                    k = max(k, j + 1);
                    cnt++;
                    break;
                }
            }
        }

        return cnt == n ? k : -1;
    }
};

