class Solution {
public:
int dp[1001][1001];
int fn(int i, vector<int> &values,int sum){
    if(sum==0) return i-1;  
    if(sum<0 || i>=values.size()) return INT_MAX;
    if(dp[i][sum]!=-1) return dp[i][sum];
    return dp[i][sum] = min(fn(i+1,values,sum-values[i]),fn(i+1,values,sum));
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> apply(n); 
        int ind=0;
        for(auto &i:queries){
            int l=i[0],r=i[1];
            for(int j=l; j<=r; j++){
                apply[j].push_back(ind);
            }
            ind++;
        }
        for(auto &j:apply){
            sort(j.begin(),j.end());
        }
        int ans=INT_MIN;
        ind=0;
        for(auto &i:apply){
            vector<int> values;
            for(auto &j:i){
                values.push_back(queries[j][2]);
            }
            memset(dp,-1,sizeof(dp));
            int p=fn(0,values,nums[ind]);
            if(p==INT_MAX) return -1;
            if(p>=0) ans=max(ans,i[p]+1);           
            ind++;
        }
        return ans==INT_MIN?0:ans;
    }
};

