class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        //time O(nlogn), space O(n)
        int n = nums.size(), qn = q.size();
        vector<vector<pair<int, int>>> v(n);
        for(int i = 0; i < qn; i++) {
            int left = q[i][0], right = q[i][1], val = q[i][2];
            for(int j = left; j <= right; j++) {
                v[j].push_back({i, val});
            }
        }        
        int res = 0;
        for(int i = 0; i < n; i++) {
            int ret = helper(nums[i], v[i]);
            if(ret == -1) return -1;
            res = max(res, ret);
        }
        return res;
    }

    int helper(int target, vector<pair<int, int>>& q) {
        if(target == 0) return 0;
        vector<bool> dp(target + 1, false);
        dp[0] = true;
        for(auto& p : q) {
            int idx = p.first, val = p.second;
            for(int i = target - val; i >= 0; i--) {
                if(dp[i]) dp[i + val] = true;
            }
            if(dp[target]) return idx + 1;
        }
        return -1;
    }
};

