class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        auto can = [&](int k) -> bool {
            for (int y = 0; y < n; y++) {
                vector<int> x;
                for (int i = 0; i < k; i++) {
                    if (queries[i][0] <= y && y <= queries[i][1])
                        x.push_back(queries[i][2]);
                }
                unordered_set<int> dp;
                dp.insert(0);
                for (int z : x) {
                    unordered_set<int> ndp = dp;
                    for (int v : dp)
                        if (v + z <= nums[y])
                            ndp.insert(v + z);
                    dp = move(ndp);
                }
                if (!dp.count(nums[y]))
                    return false;
            }
            return true;
        };
        int lo = 0, hi = m + 1, ans = -1;
        while (lo < hi) {
            int mid = (lo + hi) / 2;
            if (can(mid)) {
                ans = mid;
                hi = mid;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

