class Solution
{
public:
    bool fun(int num, const multiset<int> &subset)
    {
        if (num == 0)
        {
            return true;
        }
        if (subset.size() == 0)
        {
            return false;
        }
        vi st1(subset.begin(), subset.end());
        int sum = accumulate(st1.begin(), st1.end(), 0);
        if (sum < num)
        {
            return false;
        }
        if (sum == num)
        {
            return true;
        }
        vector<bool> dp(num + 1, false);
        dp[0] = true;
        for (int val : st1)
        {
            for (int j = num; j >= val; j--)
            {
                dp[j] = dp[j] | dp[j - val];
            }
        }
        return dp[num];
    }
    int minZeroArray(vi &nums, vector<vi> &queries)
