        {
            return 0;
        }
        int lo = 0, hi = m - 1, ans = -1;
        while (lo <= hi)
        {
            int mid = (lo + hi) / 2;
            map<int, multiset<int>> mp;
            for (int j = 0; j <= mid; j++)
            {
                for (int i = queries[j][0]; i <= queries[j][1]; i++)
