bool exist(int sum , vector<int> &arr) {
    int n = arr.size();

    // Create a 2D vector for storing results
      // of subproblems
    vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));

    // If sum is 0, then answer is true (empty subset)
    for (int i = 0; i <= n; i++)
        dp[i][0] = true;

    // Fill the dp table in bottom-up manner
    for (int i = 1; i <= n; i++) {

        for (int j = 1; j <= sum; j++) {
            if (j < arr[i - 1]) {

               // Exclude the current element
                dp[i][j] = dp[i - 1][j]; 
            }
            else {

               // Include or exclude
                dp[i][j] = dp[i - 1][j] 
                || dp[i - 1][j - arr[i - 1]];
            }
        }
    }

    return dp[n][sum];
}
class Solution {
