        for(auto ele : nums){
            if(ele!=0) zer = false;
        }
        if(zer) return 0;
        while(low<=high){
            int mid = low +  ((high-low)/2);
            bool ans  = true;
            for(i=0;i<nums.size();i++){
                vector<int> temp;
                for(j=0;j<=mid;j++){
                    if(i>=queries[j][0] && i<=queries[j][1]){
                        temp.push_back(queries[j][2]);
                    }
                }
                /*cout<<nums[i]<<"n";
                for(auto ele : temp) cout<<ele<<" ";
                cout<<"n";*/
                ans = ans & (exist(nums[i],temp));
                // cout<<ans<<"n";
            }
            if(ans){
                gans = mid ; high = mid - 1;
            }
            else{
                low = mid + 1;
            }
        }
        return gans+1;
    }
};

