class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # vali: pick any val in querys, until it is 0. record i
        # if a number can be a sum by a subset of numbers
        if all(num ==0 for num in nums):
            return 0
        def check(candidates:List[int], target:int) -> bool:
            if target == 0:
                return True
            p = {0}
            for c in candidates:
                p_new = set()
                for n1 in p:
                    if n1+c <target:
                        p_new.add(n1+c)
                    elif n1+c == target:
                        return True

                p = p|p_new
            return False

        require= [math.inf for _ in range(len(nums))]
        numCandidates = [[] for _ in range(len(nums))]
        for i, (l, r, val) in enumerate(queries):
            for _idx in range(l, r+1):
                numCandidates[_idx].append(val)
            for j, num in enumerate(nums):
                if require[j] != math.inf:
                    continue
                # find if num can meet the need to get the sum of num
                if check(numCandidates[j], num):
                    require[j]=i+1
            if max(require) != math.inf:
                return i+1
        return -1

