import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = 0;
        Map<Integer, List<Integer>> decrement = new HashMap<>();
        if (isZero(nums, decrement)) {
            return ans;
        }
        for (int[] query : queries) {
            ans++;
//            System.out.println("query:"+ans);
            for (int i = query[0]; i <= query[1]; i++) {
                decrement.computeIfAbsent(i, key -> new ArrayList<>()).add(query[2]);
            }
            if (isZero(nums, decrement)) {
                return ans;
            }
        }
        return -1;
    }

    private boolean isZero(int[] nums, Map<Integer, List<Integer>> decrement) {
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == 0) {
                continue;
            }
            List<Integer> array = decrement.get(i);
            if (array == null) {
//                System.out.println("array null");
                return false;
            }
            int target = nums[i];
            int m = array.size();
            int[][] dp = new int[m][target + 1];
            for(int[] row: dp){
                Arrays.fill(row,-1);
            }
            if (!subsetSum(m - 1, target, array, dp)) {
//                System.out.println(target + " " + array);

                return false;
            }
        }
        return true;
    }

    private boolean subsetSum(int i, int target, List<Integer> array, int[][] dp) {
        if (target == 0) {
            return true;
        }
        if (i == 0) {
            return array.get(i) == target;
        }
        if (dp[i][target] != -1) {
            return dp[i][target] == 1;
        }
        boolean first = subsetSum(i - 1, target, array, dp);
        boolean second = false;
        if (array.get(i) <= target) {
            second = subsetSum(i - 1, target - array.get(i), array, dp);
        }
        dp[i][target] = first || second ? 1 : 0;
        return first || second;
    }
}

