class Solution {
public:
    bool isSubsetSumRec(vector<int>& arr, int n, int sum, vector<vector<int>> &memo) {

        if (sum == 0)
            return 1;
        if (n <= 0)
            return 0;
        if (memo[n][sum] != -1)
            return memo[n][sum];
        if (arr[n - 1] > sum)
            return memo[n][sum] = isSubsetSumRec(arr, n - 1, sum, memo);
        else {
            return memo[n][sum] = isSubsetSumRec(arr, n - 1, sum, memo) ||
                                  isSubsetSumRec(arr, n - 1, sum - arr[n - 1], memo);
        }
    }

    bool isSubsetSum(vector<int>&arr, int sum, int n) {

        vector<vector<int>> memo(n + 1, vector<int>(sum + 1, -1));
        return isSubsetSumRec(arr, n, sum, memo);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), i,j, q = queries.size();
        vector<int> temp;
        int found = 0;
        int res = 0;
        vector<int> v(q);
        for(i=0;i<n;i++){
            int hi = q, lo = 0, mid;
            for(j=0;j<q;j++){
                if(i>=queries[j][0]&&i<=queries[j][1]){
                    v[j] = queries[j][2];
                }
                else{
                    v[j] = 0;
                }
            }
            int ans=-1;
            while(lo<=hi){
                mid = lo+(hi-lo)/2;
                if(isSubsetSum(v,nums[i],mid)){
                    found = 1;
                    ans = mid;
                    hi = mid-1;
                }
                else{
                    lo = mid+1;
                }
            }
            if(ans==-1){
                return -1;
            }
            res = max(res,ans);
        }
        return res;
    }
};

