class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp = new boolean[n][1001];

        for (int j = 0; j < n; j++) {
            dp[j][0] = true;
        }

        boolean allZeroes = Arrays.stream(nums).allMatch(num -> num == 0);
        if (allZeroes) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int i = l; i <= r; i++) {
                boolean[] newDp = new boolean[1001];

                for (int pos = 0; pos + v < 1001; pos++) {
                    if (dp[i][pos]) {
                        newDp[pos] = true;
                        newDp[pos + v] = true;
                    }
                }

                dp[i] = newDp;
                Arrays.fill(dp[i], nums[i] + 1, 1001, false);
            }

            boolean allDone = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) {
                    allDone = false;
                    break;
                }
            }

            if (allDone) return k + 1;
        }
        return -1;
    }
}

