class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int sum = 0;
        for(int i = 0; i < nums.size(); i++){
            sum += nums[i];
        }
        if(sum == 0){
            return 0;
        }
        // For each one of the nums, find a subsequence of queries that sums to num
        map<int,vector<pair<int,int>>> mp;
        int i = 1;
        for(vector<int> q : queries){
            int L = q[0];
            int R = q[1];
            int decr = q[2];
            for(int j = L; j <= R; j++){
                mp[j].push_back({decr,i});
            }
            i++;
        }
        int ans = -1;
        for(int i = 0; i < nums.size(); i++){
            if(nums[i] > 0 && !mp.count(i)){
                return -1;
            }
        }
        for(auto info : mp){
            //cout << "Now dealing with index " << info.first << "n";
            int initNum = nums[info.first];
            vector<pair<int,int>> v = info.second;
            int N = v.size();
            int dp[N][initNum+1];
            for(int j = 0; j < N; j++){
                for(int num = 0; num < initNum+1; num++){
                    dp[j][num] = 0;
                }
            }
            // dp[j][num] is number of subsequences of v up to index j with sum num
            int sol = -1;
            for(int j = 0; j < N; j++){
                dp[j][0] = 1;
                if(v[j].first <= initNum) dp[j][v[j].first] = 1;
                for(int num = 0; num <= initNum; num++){
                    if(j > 0){
                        dp[j][num] = (((((num >= v[j].first) ? dp[j-1][num - v[j].first] : 0) + dp[j-1][num])) > 0) ? 1 : 0;
                        //cout << "Number of ways to reach " << num << " by query # " << j << "is " << dp[j][num] << "n";
                    }
                    if(num == initNum && dp[j][num] > 0){
                        sol = (sol == -1) ? v[j].second : sol;

                    }
                }
            }
            //cout << "Found sol " << sol << "n";
            if(sol == -1 && initNum > 0){
                return -1;
            }
            if(sol != -1) ans = max(sol,ans);

        }
        return ans;
    }
};

