class Solution {
    public boolean helper(List<Integer> ls,int sum,int i,int[][] dp){
        if(sum==0)return true;
        if(i>=ls.size())return false;
        if(dp[i][sum]!=-1){
            if(dp[i][sum]==1)return true;
            return false;
        }
        boolean x=helper(ls,sum,i+1,dp);
        if(x){
            dp[i][sum]=1;
            return true;
        }
        if(ls.get(i)>sum){
            dp[i][sum]=0;
            return false;
        }
        x=helper(ls,sum-ls.get(i),i+1,dp);
        if(x){
            dp[i][sum]=1;
        }else{
            dp[i][sum]=0;
        }
        return x;
    }
    public boolean check(HashMap<Integer,Integer> hm,int sum){
        List<Integer> ls=new ArrayList<>();
        for(Map.Entry<Integer,Integer> h:hm.entrySet()){
            for(int i=0;i<h.getValue();i++)ls.add(h.getKey());
        }
        //if(ls.size()>0)System.out.println(ls.get(0)+" "+ls.get(1));
        int[][] dp=new int[ls.size()][sum+1];
        for(int[] d:dp)Arrays.fill(d,-1);
        return helper(ls,sum,0,dp);
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int sum=0,k=0,i=0;
        ArrayList<Integer>[] adi=new ArrayList[nums.length+1];  
        for( i=0;i<nums.length+1;i++)adi[i]=new ArrayList<>();
        ArrayList<Integer>[] del=new ArrayList[nums.length+1];
        for( i=0;i<nums.length+1;i++)del[i]=new ArrayList<>();
        HashMap<Integer,Integer> hm=new HashMap<>();
        i=0;
        for(;i<nums.length;i++){
            for(Integer d:del[i]){
                if(hm.get(d)==1){
                    hm.remove(d);
                }else{
                    hm.put(d,hm.get(d)-1);
                }
            }
            for(Integer d:adi[i]){
                hm.put(d,hm.getOrDefault(d,0)+1);
            }
            while(!check(hm,nums[i]) && k <queries.length){
                if(queries[k][1]<i){
                    k++;
                    continue;
                }
               // System.out.println(i+" gh"+k +" "+hm.size());
                if(queries[k][0]<=i){
                    hm.put(queries[k][2],hm.getOrDefault(queries[k][2],0)+1);
                   // adi[i].add(queries[k][2]);
                }else{
                    adi[queries[k][0]].add(queries[k][2]);
                }
                del[queries[k][1]+1].add(queries[k][2]);
                k++;
               // System.out.println(i+" gh"+k +" "+hm.size());

            }
            if(!check(hm,nums[i])){
                return -1;
            }
        }
        return k;
    }
}

