class Solution {
public:
    int dp[1002][1002];
    int ks(int val, int ind, vector<int>& vals, int n){
        if(val<0) return 0;
        if(val==0) return 1;
        if(ind==n){
            if(val==0) return 1;
            else return 0;
        }
        if(dp[ind][val]!=-1) return dp[ind][val];
        return dp[ind][val]= ks(val,ind+1,vals,n) || ks(val-vals[ind],ind+1,vals,n);
    }
    bool solve2(vector<int>& nums, int mid, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> ind(n);
        for (int k = 0; k <= mid; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int val = queries[k][2];
            for (int i = l; i <= r; i++)
                ind[i].push_back(val);
        }
        int cnt=0;
        for(int i=0;i<nums.size();i++){
            memset(dp,-1,sizeof(dp));
            if(ks(nums[i],0,ind[i],ind[i].size())) cnt++;
        }
        return cnt==n;
    }
    int solve(vector<int>& nums, int mid, vector<vector<int>>& queries) {
        int n = nums.size();
        // cout<<"mid "<<mid<<endl;
        vector<int> fct(n, 0);
        for (int i = 0; i <= mid; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            fct[l] -= queries[i][2];
            if (r < n - 1)
                fct[r + 1] += queries[i][2];
        }
        int cnt = 0, fact = 0, cntneg = 0;
        for (int i = 0; i < n; i++) {
            // cout<<fct[i]<<endl;
            fact += fct[i];
            // nums[i]+=fact;
            if (nums[i] + fact == 0)
                cnt++;
            else if (nums[i] + fact < 0)
                cntneg++;
            else {
            }
        }
        if (cnt == n)
            return 1;
        else if (cntneg > 0)
            return -1;
        else
            return 0;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = queries.size();
        int m = nums.size();
        bool fl = true;
        for (auto& x : nums)
            if (x > 0)
                fl = false;
        if (fl)
            return 0;
        int s = 0, e = n - 1;
        int ans = -2;
        while (s <= e) {
            // cout<<"s "<<s<<" e"<<e<<endl;
            int mid = s + (e - s) / 2;
            if (solve2(nums, mid, queries)) {
                ans = mid;
                e=mid-1;
            } else {
                s=mid+1;
            }
        }
        if(ans<0) return -1;
        return ans+1;
    }
};

