class Solution {
public:
    bool bagProblem(vector<int>& choice, int target) {
        // cout << "Resolving 0/1: target - " << target;
        // cout << " The choice: ";
        // for(auto& c: choice) {
        //     cout << c << " ";
        // }
        // cout << endl;

        vector<int> dp(target+1, 0);
        dp[0] = 1;
        for(int& num: choice) {
            for(int i = target; i >= num; i -= 1) {
                dp[i] = dp[i] || dp[i - num];
            }
        }
        // if(dp[target] == 1)
        //     cout << " SUCCESS! " <<endl;
        return dp[target];
    }

    bool validate(vector<int>& nums,  vector<vector<int>>& queries, vector<vector<int>>& choose, int qlimit) {
        // 0/1 bag problem
        vector<vector<int>> choices(nums.size());
        for(int i = 0; i < choose.size(); i += 1) {
            for(int j = 0; j < choose[i].size(); j += 1) {
                int q_idx = choose[i][j];
                if(q_idx <= qlimit)
                    choices[i].push_back(queries[q_idx][2]);
            }
        }

        // now choices means, for each idx of nums, which numbers I could choose to combine from it?
        for(int i = 0; i < nums.size(); i += 1) {
            int target = nums[i];
            if(target == 0)
                continue;
            if(!bagProblem(choices[i], target)){
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int allZero = 1;
        for(int& num: nums) {
            if(num != 0) {
                allZero = 0;
            }
        }
        if(allZero)
            return 0;


        // for each i of nums, find out the idx of the queries which includes i;
        vector<vector<int>> choose(nums.size());
        for(int j = 0; j < queries.size(); j += 1) {
            auto q = queries[j];
            int s = q[0];
            int e = q[1];
            for(int i = s; i <= e; i += 1) {
                choose[i].push_back(j);
            }
        }

        // binary search the result.
        // the result should be the ans + 1 since it is nth not the idx
        int beg = 0;
        int end = queries.size() - 1;
        while(beg <= end) {
            int mid = (beg + end) / 2;
            // mid means the last queries should be considered
            if(validate(nums, queries, choose, mid)) {
                // works, we should find smaller!
                end = mid - 1;
            } else {
                // not works, then we should find bigger 
                beg = mid + 1;
            }
        }

        // the beg is the first idx of queries limit which make it work!
        // so the answer should be beg + 1
        if(beg < queries.size()) {
            return beg + 1;
        } else {
            // invalid idx
            return -1;
        }
    }
};

