using namespace std;

class Solution {
public:
    bool good(int mid, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        for (int i = 0; i < n; i++){
            if(nums[i] == 0)
                continue;

            vector<int> curr;
            for(int j = 0; j <= mid; j++){
                if (queries[j][0] <= i && i <= queries[j][1])
                    curr.push_back(queries[j][2]);
            }

            bitset<1100> dp;
            dp.reset();
            dp[0] = 1;
            for(int v : curr){
                dp |= (dp << v);
            }
            if(!dp[nums[i]])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        bool zero = true;
        for(int x : nums){
            if(x != 0){ zero = false; break; }
        }
        if(zero)
            return 0;

        int s = -1, e = queries.size();
        while(e > s + 1){
            int mid = (s + e) / 2;
            if (good(mid, nums, queries))
                e = mid;
            else
                s = mid;
        }
        return (e == queries.size() ? -1 : e + 1);
    }
};

