class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int N=nums.length;
        int k=0;
        int Q=queries.length;
        int[][]dp=new int[1001][1001];
        for(int i=0;i<N;i++){
            if(nums[i]==0)continue;
            for(int[]row:dp)Arrays.fill(row,-1);
            k=Math.max(k, rec(nums[i], 0, queries, i, dp));
        }
        return k==Q+1?-1:k;
    }
    int rec(int X,int I,int[][]A,int p,int[][]dp){
        int Q=A.length;
        if(X==0)return I;
        if(I==Q)return Q+1;
        if(dp[X][I]!=-1)return dp[X][I];
        int[]q=A[I];
        int l=q[0],r=q[1],v=q[2];
        int o1=rec(X,I+1,A,p,dp);
        int o2=Q+1;
        if(p>=l && p<=r && X>=v){
            o2=rec(X-v,I+1,A,p,dp);
        }
        return dp[X][I]=Math.min(o1,o2);
    }
}

