class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        int zeroCount = 0;
        boolean[] isZero = new boolean[nums.length]; // indicates whether nums[i] is 0 or not
        // combs[i] stores the all possible combinations that we could have for nums[i] after a given query is applied on it
        HashSet<Integer>[] combs = new HashSet[nums.length]; 
        for(int i=0;i<nums.length;i++){
            HashSet<Integer> hs = new HashSet<>();
            hs.add(nums[i]);
            combs[i] = hs;
            if(nums[i] == 0){
                zeroCount += 1;
                isZero[i] = true;
            }
        }

        int k = 0;
        if(zeroCount == nums.length)
            return k;

        while(k < queries.length){
            int[] query = queries[k];
            for(int i=query[0];i<=query[1];i++){
                if(!isZero[i]){
                    HashSet<Integer> values = combs[i];
                    HashSet<Integer> newValues = new HashSet<>(values);
                    // Iterator<Integer> iterator = values.iterator();
                    for(int val: values){
                        // int val = iterator.next();
                        int newValue = val - query[2];
                        if(newValue == 0){
                            isZero[i] = true;
                            zeroCount += 1;
                            break;
                        }
                        else if(newValue > 0)
                            newValues.add(val - query[2]);
                    }
                    combs[i] = newValues;
                }
            }
            k++;
            if(zeroCount == nums.length)
                return k;
        }

        return -1;
    }
}

