class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ptr_q = 0
        m = len(queries)
        n = len(nums)
        mem = [set([0]) for _ in range(n)]
        for ind, num in enumerate(nums):
            while ptr_q < m and num not in mem[ind]:
                l, r, val = queries[ptr_q]
                ptr_q += 1
                if r < ind:
                    continue
                for ptr in range(max(ind, l), r+1):
                    temp = set()
                    for pre in mem[ptr]:
                        temp.add(pre+val)
                    mem[ptr] |= temp
            if ptr_q == m and num not in mem[ind]:
                return -1
        return ptr_q

