class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int m = queries.length, n = nums.length;
        int logic[][] = new int[m][n];

        for (int i = 0; i < m; ++i) {
            int start = queries[i][0], end = queries[i][1];
            for (int j = start; j <= end; ++j)
                logic[i][j] = queries[i][2];
        }

        List<Set<Integer>> possibleNums = new ArrayList<Set<Integer>>();

        int count = 0, k = 0;
        for (int i = 0; i < n; ++i) {
            Set<Integer> set = new HashSet<Integer>();
            set.add(nums[i]);
            possibleNums.add(set);
            if (nums[i] == 0) count++;
        }
        if (count == n) return 0;

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                int num = nums[j];
                if (logic[i][j] == 0) continue;
                if (logic[i][j] > num) continue;
                Set<Integer> set = possibleNums.get(j);
                if (set.contains(0)) continue;
                if (set.contains(logic[i][j])) {
                    count++;
                    set.add(0);
                } else {
                    Set<Integer> newSet = new HashSet<>(set);
                    for (int x : newSet) {
                        if (x > logic[i][j])
                            set.add(x-logic[i][j]);
                    }
                }
            }
            k++;
            if (count == n) {
                return k;
            }
        }

        return -1;
    }
}

