import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        // Check if all elements are zero
        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) {
            return 0;
        }

        int maxM = -1;

        for (int i = 0; i < nums.length; i++) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }

            // Collect all queries that include i in their range
            List<int[]> qs = new ArrayList<>();
            for (int j = 0; j < queries.length; j++) {
                int[] q = queries[j];
                int l = q[0], r = q[1];
                if (i >= l && i <= r) {
                    qs.add(new int[]{q[2], j}); // store val and original index
                }
            }

            if (qs.isEmpty()) {
                return -1;
            }

            // Subset sum DP to find the minimal latest original index
            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);

            for (int[] q : qs) {
                int val = q[0];
                int origPos = q[1];
                Map<Integer, Integer> newDp = new HashMap<>(dp);

                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int s = entry.getKey();
                    int latest = entry.getValue();
                    int newSum = s + val;
                    int newLatest = Math.max(latest, origPos);

                    if (!newDp.containsKey(newSum) || newLatest < newDp.getOrDefault(newSum, Integer.MAX_VALUE)) {
                        newDp.put(newSum, newLatest);
                    }
                }

                dp = newDp;
            }

            if (dp.containsKey(target)) {
                int mi = dp.get(target);
                maxM = Math.max(maxM, mi);
            } else {
                return -1;
            }
        }
        return maxM + 1;
    }
}

