class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        int[] r = new int[nums.length];

        for(int i = 0; i < nums.length; i ++) {

            r[i] = -1;

            boolean[][] A = new boolean[queries.length + 1][nums[i] + 1];
            A[0][0] = true;
            if (nums[i] == 0)   
                r[i] = 0;
            else {
                for(int j = 1; j <= queries.length; j ++) {

                    for(int k = 0; k <= nums[i]; k ++) {
                        if ((i >= queries[j - 1][0]) && (i <= queries[j - 1][1]) && (k >= queries[j - 1][2])) {
                            A[j][k] = A[j - 1][k] || A[j - 1][k - queries[j - 1][2]];
                        } else {
                            A[j][k] = A[j - 1][k];
                        }
                    }

                    if (A[j][nums[i]]) {
                        r[i] = j;
                        break;
                    }

                }
            }

        }

        int m = r[0];
        for(int rr : r) {
            if (rr == -1)
                return -1;
            m = Math.max(rr, m);
        }
        return m;

    }
}

