class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        res, n, m = -1, len(nums), len(queries)
        qs = [[] for _ in range(n)]
        for q in range(m):
            l, r, val = queries[q]
            for i in range(l, r + 1):
                qs[i].append((q, val))

        def can_zero(k):
            for i in range(n):
                if nums[i] == 0:
                    continue
                vals = [val for q, val in qs[i] if q < k]
                if not vals and nums[i] > 0:
                    return False
                target = nums[i]
                dp = [False] * (target + 1)
                dp[0] = True
                for val in vals:
                    for s in range(target, val - 1, -1):
                        dp[s] = dp[s] or dp[s - val]
                if not dp[target]:
                    return False
            return True
        l, r = 0, len(queries)
        while l <= r:
            mid = (l + r) // 2
            if can_zero(mid):
                res = mid
                r = mid - 1
            else:
                l = mid + 1
        return res

