class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<vector<pair<int, int>>> queries_for_index(n);
        for (int j = 0; j < m; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; ++i) {
                queries_for_index[i].emplace_back(val, j);
            }
        }
        int ans = -1;
        for (int i = 0; i < n; i ++) {
            if (nums[i] == 0) continue;
            int target = nums[i];
            auto& q = queries_for_index[i];
            if (q.empty()) return -1;

            unordered_set<int> cur = {0};
            bool found = false;
            int last_j = -1;

            for (auto& [val, j] : q) {
                unordered_set<int> new_sums;
                for (int s : cur) {
                    int new_s = s + val;
                    if (new_s == target) {
                        found = true;
                        last_j = j;
                        break;
                    }
                    if (new_s < target) {
                        new_sums.insert(new_s);
                    }
                }
                if (found) break;
                for (int s : new_sums) {
                    cur.insert(s);
                }
            }
            if (!found) return -1;
            ans = max(ans, last_j);
        }
        if (ans == -1) return 0;
        return ans + 1;
    }
};

