class Solution {

    public int minZeroArray(int[] nums, int[][] queries) {
        int m = nums.length;
        int qLen = queries.length;
        //  index  query  (: [queryIndex, vali])
        List<List<int[]>> posQueries = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            posQueries.add(new ArrayList<>());
        }
        //  query  queries 
        for (int qi = 0; qi < qLen; qi++) {
            int l = queries[qi][0];
            int r = queries[qi][1];
            int val = queries[qi][2];
            for (int i = l; i <= r; i++) {
                //  index i query
                posQueries.get(i).add(new int[]{qi, val});
            }
        }

        int overallK = 0;
        //  index  DP query  nums[i]
        //  query  query 
        for (int i = 0; i < m; i++) {
            int target = nums[i];
            List<int[]> qList = posQueries.get(i);
            //  query  query 
            qList.sort((a, b) -> Integer.compare(a[0], b[0]));

            // dp[s]  sum = s  query  INF
            int INF = Integer.MAX_VALUE / 2;
            int[] dp = new int[target + 1];
            Arrays.fill(dp, INF);
            dp[0] = -1; //  query query  -1

            //  query 
            for (int[] curQuery : qList) {
                int qi = curQuery[0];
                int val = curQuery[1];
                //  query
                for (int s = target - val; s >= 0; s--) {
                    if (dp[s] != INF) {
                        int ns = s + val;
                        //  query query  max(dp[s], qi)
                        dp[ns] = Math.min(dp[ns], Math.max(dp[s], qi));
                    }
                }
            }

            //  nums 
            if (dp[target] == INF) return -1;
            //  query  0  1
            overallK = Math.max(overallK, dp[target] + 1);
        }

        return overallK;
    }

}

