class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = -1;
        int[][] memoArr = new int[nums.length][1003];
        int zeros = 0;
        for(int i=0;i<nums.length;i++) {
            if(nums[i]==0) {
                ++zeros;
            }
        }
        if(zeros==nums.length) {
            return 0;
        }
        for(int i=0;i<queries.length;i++) {
            int[] query = queries[i];
            for(int j=query[0];j<=query[1];j++) {
                if(nums[j]==0) {
                    continue;
                }
                int[] memo = memoArr[j];
                //0->false
                //1->intermediateTrue
                //2->true
                int inc = query[2];
                memo[nums[j]] = 2;
                for(int k=nums[j]-1;k>=0;k--) {
                    if(memo[k]==0 && (k+inc)<=nums[j] && memo[k+inc]==2) {
                        memo[k] = 1;
                    }
                }
                for(int k=nums[j];k>=0;--k) {
                    if(memo[k]==1) {
                        memo[k]=2;
                    }
                }
                if(memo[0]==2) {
                    nums[j] = 0;
                }
            }

            int numOfZeroes = 0;
            for(int t=0;t<nums.length;t++) {
                if(nums[t]==0) {
                    ++numOfZeroes;
                }
            }
            if(numOfZeroes == nums.length) {
                return i+1;
            }
        }
        return ans;
    }
}

