class Solution {
public:
    bool canAchieve(int k, const vector<vector<int>>& queries, const vector<int>& nums) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int j = 0; j < k; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    int v = queries[j][2];
                    for (int s = target - v; s >= 0; s--) {
                        if (dp[s]) {
                            dp[s + v] = true;
                        }
                    }
                }
            }
            if (!dp[target]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();

        vector<int> numsCopy = nums;

        int left = 0, right = m, answer = -1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (canAchieve(mid, queries, numsCopy)) {
                answer = mid;
                right = mid - 1; 
            } else {
                left = mid + 1;
            }
        }
        return answer;
    }
};

