class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if nums.count(0) == len(nums):
            return 0
        left, right = 0, len(queries) - 1
        ans = -1
        def posSum(index, target, lst, dp)-> bool:
            if target == 0:
                return True
            if index == len(lst):
                return False
            if dp[index][target] != -1:
                return dp[index][target]
            sub = lst[index][0]
            ans = False
            for j in range(lst[index][1]):
                if sub > target:
                    break
                ans = ans or posSum(index + 1, target - sub, lst, dp)
                sub += lst[index][0]
                if ans:
                    break 
            if not ans:
                ans = posSum(index + 1, target, lst, dp)
            dp[index][target] = ans
            return ans

        def check(add, remove)-> bool:
            lst = defaultdict(int)
            for i in range(len(nums)):
                for key in add[i]:
                    lst[key] += add[i][key]
                for key in remove[i]:
                    lst[key] -= remove[i][key]
                dp = [[-1 for target in range(nums[i] + 1)] for index in range(len(lst))]
                if not posSum(0, nums[i], list(lst.items()), dp):
                    return False
            return True

        def zeroArray(mid):
            add = [defaultdict(int) for i in range(len(nums))]
            remove = [defaultdict(int) for i in range(len(nums) + 1)]
            for i in range(mid + 1): 
                l, r, val = queries[i]
                add[l][val] += 1
                remove[r + 1][val] += 1
            return check(add, remove)

        while left <= right:
            mid = left + (right - left) // 2
            if zeroArray(mid): 
                ans = mid
                right = mid - 1
            else:
                left = mid + 1
        if ans == -1:
            return ans
        return ans + 1

