class Solution {
    int isSubsetSum(vector<int>& arr, int target) {
        // cout<<"Call"<<endl;
        int n= arr.size();
        if(n == 0)
            return target == 0;

        vector<vector<bool>> dp(n,vector<bool>(target+1,0));
        // cout<<"Np";
        for(int i=0;i<n;i++){
            dp[i][0]=true;
        }
        // cout<<"Npn";
        // for(auto i : arr)
        //     cout<<i<<" ";
        // cout<<endl;
        dp[0][arr[0]]=true;
        if(dp[0][target])
            return 0;
        // cout<<"Np";

        for(int i=1;i<n;i++){
            for(int t =1 ;t<target+1;t++){
                bool nottake= dp[i-1][t];
                bool take = false;
                if(t>=arr[i]) take = dp[i-1][t -arr[i]];
                dp[i][t]= take || nottake;
            }
            if(dp[i][target])
                return i; 
        }

        return -1;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> op(n), values(n);
        int zeroes = count(begin(nums), end(nums), 0);
        if(zeroes == n)
            return 0;

        for(int i = 0; i < queries.size(); i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for(int j = l; j <= r; j++) {
                op[j].push_back(val);
                values[j].push_back(i+1);
            }
        }
        int maxOp = 0;
        for(int i = 0; i < n; i++) {
            if(op[i].empty()) {
                if(nums[i] != 0)
                    return -1;
                continue;
            }
            int ans = isSubsetSum(op[i], nums[i]);
            // cout<<"Call end"<<endl;
            if(ans == -1)
                return -1;
            maxOp = max(maxOp, values[i][ans]);
        }

        return maxOp;
    }
};

