class Solution {
private:
    bool isSubsetSum(vector<int> &arr, int sum) {
        int n = arr.size();
        vector<bool> prev(sum + 1, false), curr(sum + 1);

        prev[0] = true;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= sum; j++) {
                if (j < arr[i - 1])
                    curr[j] = prev[j];
                else
                    curr[j] = (prev[j] || prev[j - arr[i - 1]]);
            }
            prev = curr;
        }
        return prev[sum];
    }

    bool predicate(vector<int>& nums, vector<vector<int>>& queries, int k) {
        vector<pair<int, int>> addQ;
        vector<pair<int, int>> removeQ;

        for (int i=0 ; i<k ; i++) {
            vector<int> query = queries[i];
            addQ.push_back({query[0], query[2]});
            removeQ.push_back({query[1]+1, query[2]});
        }

        sort(addQ.begin(), addQ.end());
        sort(removeQ.begin(), removeQ.end());

        auto aitr = addQ.begin();
        auto ritr = removeQ.begin();
        vector<int> cur;
        for (int i=0 ; i<nums.size() ; i++) {
            while(aitr != addQ.end() && aitr->first <= i) {
                cur.push_back(aitr->second);
                aitr++;
            }
            while(ritr != removeQ.end() && ritr->first <= i) {
                auto it = find(cur.begin(), cur.end(), ritr->second);
                cur.erase(it);
                ritr++;
            }

            // for (int c : cur) {
            //     cout << c << " ";
            // }cout << endl;

            if (!isSubsetSum(cur, nums[i]))
                return false;
        }
        return true;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0;
        int r = queries.size() + 1;

        // predicate(nums, queries, queries.size());

        while (l < r) {
            int m = l + ((r - l) >> 1);
            if (!predicate(nums, queries, m))
                l = m + 1;
            else
                r = m;
        }

        return l >= queries.size() + 1 ? -1 : l;
    }
};

