class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool allZero = true;
        for(auto &i:nums){
            if(i != 0){
                allZero = false;
                break;
            }
        }
        if(allZero) return 0;
        int start = 1;
        int end = queries.size();
        int ans = -1;
        while(start<=end){
            int mid = start + ((end-start)/2);
            bool res = doSomething(mid,nums,queries);
            if(res){
                if(ans==-1){
                    ans = mid;
                }else{
                    ans = min(mid,ans);
                }
                end = mid-1;
            }else{
                start = mid+1;
            }
        }
        return ans;
    }

    bool doSomething(int mid,vector<int>& nums, vector<vector<int>>& queries){
        vector<vector<int>> v(nums.size());
        for(int i=0;i<mid;i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                v[j].push_back(queries[i][2]);
            }
        }
        for(int i=0;i<nums.size();i++){
            // unordered_map<string, bool> memo;
            // if(!combinationEqualsSum(v[i].size()-1,nums[i],v[i],memo)){
            //     return false;
            // }
            if(!subsetSum(v[i],nums[i])){
                return false;
            }
        }
        return true;
    }

    bool subsetSum(vector<int>& vals, int target) {
        vector<bool> dp(target + 1, false);
        dp[0] = true; // Base case: sum of 0 is always possible

        for (int val : vals) {
            for (int j = target; j >= val; --j) {  // Fill DP array in reverse
                dp[j] = dp[j] || dp[j - val];
            }
        }
        return dp[target];
    }

    bool combinationEqualsSum(int index, int sum, vector<int>& val, unordered_map<string, bool>& memo) {
        if (sum == 0) return true;
        if (index < 0) return false;
        if (index == 0) return val[0] == sum;
        string key = to_string(index) + "_" + to_string(sum);
        if (memo.find(key) != memo.end()) return memo[key];

        bool exclude = combinationEqualsSum(index - 1, sum, val, memo);

        bool include = false;
        if (val[index] <= sum) {
            include = combinationEqualsSum(index - 1, sum - val[index], val, memo);
        }

        return memo[key] = (include || exclude);
    }

};

