class Solution:
    def sss(self, target, arr):
        n = len(arr)
        if n == 0:
            return False
        dp = [[False] * (target + 1) for _ in range(n + 1)]
        for i in range(n + 1):
            dp[i][0] = True

        for i in range(1, n + 1):
            for j in range(1, target + 1):
                if j < arr[i - 1]:
                    dp[i][j] = dp[i - 1][j]
                else:
                    dp[i][j] = dp[i - 1][j] or dp[i - 1][j - arr[i - 1]]

        return dp[n][target]

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        l = len(nums)
        ql = []
        qi = 0
        for i in range(l):
            n = nums[i]
            if n == 0:
                continue
            while True:
                sql = []
                for l, r, val in ql:
                    if l <= i <= r:
                        sql.append(val)
                if self.sss(n, sql):
                    break
                if qi >= len(queries): return -1   
                ql.append(queries[qi])
                qi += 1
        return len(ql)

