/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function (nums, queries) {
    const set = new Set();
    const possibleValues = Array(nums.length);
    for (let i = 0; i < nums.length; i++) {
        possibleValues[i] = new Set();
    }
    for (let i = 0; i < nums.length; i++) {
        if (nums[i] === 0) {
            set.add(i);
            continue;
        }
        possibleValues[i].add(nums[i]);
    }
    if (set.size === nums.length) return 0;

    for (let i = 0; i < queries.length; i++) {
        const [l, r, val] = queries[i];
        for (let j = l; j <= r; j++) {
            if (set.has(j)) {
                continue;
            }

            const iterator = possibleValues[j].values();
            const size = possibleValues[j].size;
            let k = 0;
            while (k < size) {
                k++
                const { value } = iterator.next();
                if (value === val) {
                    set.add(j);
                    if (set.size === nums.length) return i + 1;
                    break;
                }

                if (value > val) {
                    possibleValues[j].add(value - val);
                }
            }
        }
    }

    return -1;
};

