class Solution {
public:
    // bool helper(int curIdx, vector<int>& nums, int target) {
    //     if (target == 0) {
    //         return true;
    //     } else if (curIdx == nums.size() || target < 0) {
    //         return false;
    //     }

    //     return helper(curIdx + 1, nums, target - nums[curIdx]) || helper(curIdx + 1, nums, target);
    // }

    bool canSumToTarget(multiset<int>& numSet, int target) {
        unordered_set<int> possibleSumSet;
        possibleSumSet.insert(target);

        multiset<int>::iterator it;
        for (it = numSet.begin(); it != numSet.end(); ++it) {
            if (possibleSumSet.count(*it) > 0) {
                return true;
            }

            unordered_set<int> tmp = possibleSumSet;
            unordered_set<int>::iterator it1;
            for (it1 = possibleSumSet.begin(); it1 != possibleSumSet.end(); ++it1) {
                tmp.insert(*it1 - *it);
            }

            possibleSumSet = tmp;
        }

        return false;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        multiset<int> opeSet;
        unordered_map<int, multiset<int>> addMap, deleteMap;
        int queryIdx = 0;

        for (int i = 0; i < nums.size(); i++) {
            if (!addMap[i].empty()) {
                opeSet.insert(addMap[i].begin(), addMap[i].end());
            }

            if (!deleteMap[i].empty()) {
                multiset<int>::iterator it;
                for (it = deleteMap[i].begin(); it != deleteMap[i].end(); ++it) {
                    opeSet.erase(opeSet.find(*it));
                }
            }

            if (nums[i] > 0) {
                while (!canSumToTarget(opeSet, nums[i])) {
                    if (queryIdx == queries.size()) {
                        return -1;
                    }

                    int left = queries[queryIdx][0];
                    int right = queries[queryIdx][1];
                    int value = queries[queryIdx][2];

                    if (left > i) {
                        addMap[left].insert(value);
                        deleteMap[right + 1].insert(value);
                    } else if (i <= right) {
                        opeSet.insert(value);
                        deleteMap[right + 1].insert(value);
                    }

                    queryIdx++;
                }
            }
        }

        return queryIdx;
    }
};

