class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        int zeros = 0;
        var vs = new boolean[n];
        Set<Integer>[] dp = new Set[n];
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                vs[i] = true;
                zeros++;
            } else {
                dp[i] = new HashSet<>();
                dp[i].add(nums[i]);
            }
        }

        if (zeros == n) return 0;

        int answ = 0;
        for (var q : queries) {
            for (int i = q[0]; i <= q[1]; i++) {
                if (vs[i]) continue;
                var arr = dp[i].toArray(new Integer[0]);
                for (var num : arr) {
                    int res = num - q[2];
                    if (res == 0) {
                        vs[i] = true;
                        zeros++;
                        break;
                    } else if (res > 0) {
                        dp[i].add(res);
                    }
                }
            }
            answ++;
            if (zeros == n) break;
        }

        return zeros == n ? answ : -1;
    }
}

