class Solution {
public:

    long long func(long long i,long long sum,vector<long long> &wow,vector<vector<long long>> &dp){
        if(i==wow.size()){
            return sum==0;
        }
        if(dp[i][sum]!=-1) return dp[i][sum];
        long long x1=0,x2=0;
        if(sum>=wow[i]){
            x1=func(i+1,sum-wow[i],wow,dp);
        }
        x2=func(i+1,sum,wow,dp);
        return dp[i][sum]=max(x1,x2);
    }

    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        long long i,j,sum=0,ans=-1;
        long long n=v.size();
        long long m=q.size();

        long long low=0;
        long long high=m-1;
        long long mid;

        for(i=0;i<n;i++){
            if(v[i]==0) sum++;
        }
        if(sum==n) return 0;
        sum=0;
        while(low<=high){
            sum=0;
            mid=(low+high)/2;

            for(i=0;i<n;i++){
                if(v[i]==0){
                    sum++;
                    continue;
                }
                vector<long long> wow;
                for(j=0;j<=mid;j++){
                    if(i>=q[j][0] && i<=q[j][1]) wow.push_back(q[j][2]);
                }
                if(wow.size()==0) continue;
                sort(wow.begin(),wow.end());
                if(wow[0]>v[i]) continue;
                vector<vector<long long>> dp(wow.size(),vector<long long> (v[i]+1,-1));
                sum+=func(0,v[i],wow,dp);
            }

            if(sum==n){
                ans=mid+1;
                high=mid-1;
            }
            else{
                low=mid+1;
            }
        }

        // if(ans==0) ans--;
        return ans;
    }
};

