class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        nq = len(queries)
        if self.checkZero(nums, queries, nq) == False:
            return -1
        left = 0
        right = nq
        while left < right:
            mid = left + (right - left)//2
            if self.checkZero(nums, queries, mid):
                right = mid
            else:
                left = mid + 1
        return left

    def checkZero(self, nums: List[int], queries: List[List[int]], k) -> bool:
        d = []
        for i in range(len(nums)):
            d.append([0]*11)
        for i in range(k):
            q = queries[i]
            for j in range(q[0], q[1]+1):
                d[j][q[2]] += 1
        for i in range(len(nums)):
            tar = nums[i]
            if tar == 0:
                continue
            val = d[i]
            if max(val) == 0:
                return False
            if self.check_combine(tar, val):
                continue
            else:
                return False
        return True

    def check_combine(self, tar: int, val: List[int]) -> bool:
        if tar <= 10:
            if val[tar] > 0:
                return True
        max_sum = sum(val[i]*i for i in range(len(val)))
        if max_sum < tar:
            return False
        for i in range(10, 0, -1):
            if tar < i:
                continue
            if val[i] > 0:
                tar -= i
                val[i] -= 1
                if self.check_combine(tar, val):
                    return True
                tar += i
                val[i] += 1
        return False

