#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    unordered_map<int, int> memo;

    int helper(int sum, vector<int>& a, int ind) {
        if (sum == 0) return 0;
        if (ind >= a.size()) return 1e7;

        int key = sum * 1000 + ind; 
        if (memo.count(key)) return memo[key];

        int cnt = 1e7;
        if (sum >= a[ind]) {
            cnt = min(cnt, 1 + helper(sum - a[ind], a, ind + 1));
        }
        cnt = min(cnt, 1+helper(sum, a, ind + 1));

        return memo[key] = cnt;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        vector<vector<int>> v(n,vector<int>(q));

        for (int j = 0; j < q; j++) {
            int l = queries[j][0], r = queries[j][1], val = queries[j][2];
            for (int i = 0; i <n; i++) {
                if(i>=l && i<=r)v[i][j]=val;
                else v[i][j]=0;
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            memo.clear();
            ans = max(ans, helper(nums[i], v[i], 0));
        }

        return (ans > q) ? -1 : ans;
    }
};

