class Solution {
public:
    vector<vector<int>> denomination;
    int dp[11][1001][1001];
    bool isPossible(int i, int target, int n) {
        if (target == 0) return true;
        if (n == 0) return false;
        if(dp[i][target][n]!=-1)
            return dp[i][target][n];
        if (denomination[i][n - 1] > target) {
            return dp[i][target][n] = isPossible(i,target, n - 1);
        }
        return dp[i][target][n] = isPossible(i,target, n - 1) || isPossible(i,target - denomination[i][n - 1], n - 1);
    }
    bool check(int mid,vector<int>& nums){
        int n = nums.size();
        for(int i=0;i<n;i++)
            if(isPossible(i,nums[i],mid+1)==false)
                return false;
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if(accumulate(nums.begin(),nums.end(),0)==0)
            return 0;
        int q = queries.size(), n = nums.size();
        denomination.resize(n);
        memset(dp,-1,sizeof(dp));
        for(int i=0;i<q;i++){
            for(int j=0;j<n;j++)
                if(j>=queries[i][0] and j<=queries[i][1])
                    denomination[j].push_back(queries[i].back());
                else
                    denomination[j].push_back(0);
        }
        int l = 0, h = q-1;
        while(l<=h){
            int mid = (l+h)/2;
            if(check(mid,nums))
                h = mid - 1;
            else
                l = mid + 1;
        }
        if(l==q)
            l=-2;
        return l+1;
    }
};

