class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(x == 0 for x in nums):
            return 0

        n = len(nums)
        m = len(queries)

        # check if we can make nums zero with first k query
        def can_zero(k):
            for j in range(n):
                target = nums[j]
                dp = [False] * (target + 1)
                dp[0] = True
                for i in range(k):
                    l, r, val = queries[i]
                    if l <= j <= r:
                        for s in range(target, val - 1, -1):
                            if dp[s - val]:
                                dp[s] = True
                if not dp[target]:
                    return False
            return True

        left, right = 1, m + 1
        ans = -1
        while left < right:
            mid = (left + right) // 2
            if can_zero(mid):
                ans = mid
                right = mid
            else:
                left = mid + 1

        return ans if ans != -1 else -1

