class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> first(n, -1);
        vector<vector<bool>> dp(n);
        for (int i = 0; i < n; i++) {
            dp[i].resize(nums[i] + 1, false);
            dp[i][0] = true;
             if (nums[i] == 0)
                first[i] = 0;
        }
        int q = queries.size();
        for (int i = 0; i < q; i++) {
            int n1 = queries[i][0], n2 = queries[i][1], value = queries[i][2];
            for (int j = n1; j <= n2; j++) {
                if (first[j] != -1)
                    continue;
                int t = nums[j];

                for (int k = t - value; k >= 0; k--) {
                    if (dp[j][k] && !dp[j][k + value]) {
                        dp[j][k + value] = true;
                    }
                }
                if (dp[j][t] && first[j] == -1)
                    first[j] = i + 1;
            }
        }
        int result = 0;
        for (int i = 0; i < n; i++) {
            if (first[i] == -1)
                return -1;
            result = max(result, first[i]);
        }
        return result;
    }
};

