class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int k = -1;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] == 0) {
                k = 0;
                continue;
            }

            int ki = -1;
            // set to store all possibilities of the current index value after every query
            set<int> s;
            s.insert(nums[i]);
            // loop through each query
            for (int j = 0; j < queries.size(); ++j) {
                int l = queries[j][0], r = queries[j][1], v = queries[j][2];
                if (i >= l && i <= r) {
                    for (auto r: s) {
                        if (r - v > 0)
                            s.insert(r - v);
                        // first time the index value reaches zero
                        else if (r - v == 0) {
                            ki = j + 1;
                            break;
                        }
                    }
                }
                if (ki > 0)
                    break;
            }

            // an index can't become zero after all queries
            if (ki == -1)
                return -1;

            // otherwise, update the maximum steps
            else
                k = max(k, ki);
        }
        return k;
    }
};

