class Solution {
private:
    int dp(vector<int>& nums, int index, vector<vector<int>>& queries, int num, int queryIndex, vector<vector<int>>& map) {
        if(!num)
            return queryIndex;

        if(queryIndex == queries.size())
            return num == 0 ? queryIndex : INT_MAX;

        if(num < 0)
            return INT_MAX;

        if(map[queryIndex][num] != INT_MIN)
            return map[queryIndex][num];

        int result{INT_MAX};

        // case 1 - ignore 
        result = min(result, dp(nums, index, queries, num, queryIndex + 1, map));

        // case 2 - use query
        if(index >= queries[queryIndex][0] && index <= queries[queryIndex][1]) {
            result = min(result, dp(nums, index, queries, num - queries[queryIndex][2], queryIndex + 1, map));
        }

        return map[queryIndex][num] = result;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int result{INT_MIN};

        for(int i = 0; i < nums.size(); i++) {
            vector<vector<int>> map(queries.size(), vector<int>(1001, INT_MIN));
            result = max(result, dp(nums, i, queries, nums[i], 0, map));
        }

        if(result == INT_MAX)
            return -1;

        return result;
    }
};

