class Solution {
public:

    bool isV(int i , int n , vector<int>& list , int tar, vector<vector<int>>& dp ) {
        if(tar == 0) return true;
        if(tar < 0) return false;
        if(i >= n) return false;
        auto& ret = dp[i][tar];
        if(ret != -1) return ret;

        bool left = isV(i + 1 , n , list , tar , dp);
        bool notleft = isV(i + 1 , n , list , tar - list[i] ,dp);

        return ret = (left || notleft);

    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        map<int , vector<int>> mp;
        map<int , vector<int>> pm;



        int n = nums.size() , m = queries.size();

        for(int i = 0 ; i < n ; i++) {
            mp[i] ={};
            pm[i] ={};
        }

        for(int i = 0 ; i  < n ; i++) {
            for(int j = 0 ; j < m ; j++) {
                if(queries[j][0] <= i && queries[j][1] >= i) {
                    mp[i].push_back(queries[j][2]);
                    pm[i].push_back(j);
                }
            }
        }

        int k =0 ;



        for(auto& it : mp) {
            int tar = nums[it.first];
            vector<int> list = it.second;
            if(list.size() == 0 && tar > 0) return -1;
            if(tar == 0) continue;
            bool ck = false;

            for(int i = 0  ; i < list.size() ; i++) {
                vector<vector<int>> dp(i + 2 , vector<int>(tar + 1 , -1));
                if(isV(0 , i + 1 , list , tar , dp)) {

                    k = max(k , pm[it.first][i] + 1);
                    ck = true;
                    break;
                 }

            }
            if(!ck) return -1;
        }

        return k;
    }
};

