class Solution {
private:
    bool isZeroState(const vector<int>& a) {

        for (auto x : a) {
            if (x != 0) return false;
        }
            return true;
    }

    bool canAchieve(const vector<bitset<1001>>& b, const vector<int>& a) {


        int sz = a.size();

        for (int i = 0; i < sz; ++i) {

            if (!b[i][a[i]]) 
            {
                return false;
            }
        }
         return true;
    }

    void applyOp(vector<bitset<1001>>& b, int s, int e, int d, const vector<int>& a) {


        for (int i = s; i <= e; ++i) {

               bitset<1001> t;

            for (int j = 0; j < 1001 - d; ++j) {

                if (b[i][j]) 
                {
                    t.set(j + d);
                }
            }

            b[i] |= t;

              int m = a[i];

            for (int j = m + 1; j < 1001; ++j) {

                    b[i].reset(j);
                }
         }
        }

public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {

        int sz = a.size();

        if (isZeroState(a)) return 0;


        vector<bitset<1001>> b(sz);

        for (int i = 0; i < sz; ++i) 

        {
            b[i].set(0);
        }

        for (int i = 0; i < q.size(); ++i) {


            applyOp(b, q[i][0], q[i][1], q[i][2], a);


                if (canAchieve(b, a)) 
                {
                    return i + 1;
                }
        }

        return -1;
    }
};

